@namespace
class SpriteKind:
    Block = SpriteKind.create()
def GenerateTerrain():
    global NoiseScale, Height, x, Noise, col
    NoiseScale = 0.001
    Height = 8
    while col <= scene.screen_width() / 8:
        x = (col + seed) * NoiseScale
        Noise = Math.floor(perlinNoise.noise1d(x) * Height) + 2
        row = 0
        while row <= scene.screen_height() / 8:
            if row == Noise:
                tiles.set_tile_at(tiles.get_tile_location(col, row), grass)
            elif row > Noise:
                tiles.set_tile_at(tiles.get_tile_location(col, row), dirt)
            row += 1
        col += 1
